  show(message) {
    const toast = this.toastCtrl.create({
      message: message,
      duration: 3000,
      position: 'middle',
    });
    toast.then((res) => {
      res.present();
    });
  }

  //status saver method
  sortByKeyDesc = (array, key) => {
    return array.sort((a, b) => {
      let x = a[key];
      let y = b[key];
      return x > y ? -1 : x < y ? 1 : 0;
    });
  };

  readWhatsappMedia() {
    
    const path = this.file.externalRootDirectory + 'WhatsApp/Media/';
    console.log("readWhatsappMedia>>>>>>>>>>>>>>>>>>>>>>>>>>>>>"+path);
    const dirName = '.Statuses';

    this.file
      .listDir(path, dirName)
      .then((entries) => {
        if (entries && entries.length) {
          let count = 0;
          const getFileInfo = () => {
            if (count < entries.length) {
              this.file
                .resolveLocalFilesystemUrl(entries[count]['nativeURL'])
                .then((fileEntry) => {
                  fileEntry.getMetadata((fileObj) => {
                    entries[count]['date'] = fileObj['modificationTime'];
                    count += 1;
                    getFileInfo();
                  });
                });
            } else {
              this.wholeWPVideo = this.sortByKeyDesc(entries, 'date');

              this.wholeWPVideo.forEach((ff, index) => {
                if (ff.nativeURL.indexOf('.jpg') > -1) {
                  this.images.push({
                    nativeURL: this.getTrustImg(ff.nativeURL),
                    isBusinessWP: false,
                    name: ff.name,
                  });
                } else if (ff.nativeURL.indexOf('.mp4') > -1) {
                  this.wholeVideos.push({
                    name: ff.name,
                    path: path + dirName,
                    isBusinessWP: false,
                  });
                }
              });
              setTimeout(() => {
                this.getWhatsappBusinessMedia();
              }, 600);
            }
          };
          getFileInfo();
        } else {
          this.wholeWPVideo = [];
          this.getWhatsappBusinessMedia();
        }
      })
      .catch((e) =>
        console.log('While reading whatsapp getting errors' + JSON.stringify(e))
      );
  }

  getWhatsappBusinessMedia() {
    // read whatsapp business

    const pathB = this.file.externalRootDirectory + 'WhatsApp Business/Media/';
    const dirNameB = '.Statuses';

    this.file
      .listDir(pathB, dirNameB)
      .then((entries) => {
        if (entries && entries.length) {
          let count = 0;
          const getFileInfo = () => {
            if (count < entries.length) {
              this.file
                .resolveLocalFilesystemUrl(entries[count]['nativeURL'])
                .then((fileEntry) => {
                  fileEntry.getMetadata((fileObj) => {
                    entries[count]['date'] = fileObj['modificationTime'];
                    count += 1;
                    getFileInfo();
                  });
                });
            } else {
              this.wholeWBData = this.sortByKeyDesc(entries, 'date');

              this.wholeWBData.forEach((ff, index) => {
                if (ff.nativeURL.indexOf('.jpg') > -1) {
                  this.images.push({
                    nativeURL: this.getTrustImg(ff.nativeURL),
                    isBusinessWP: true,
                    name: ff.name,
                  });
                } else if (ff.nativeURL.indexOf('.mp4') > -1) {
                  this.wholeVideos.push({
                    name: ff.name,
                    path: pathB + dirNameB,
                    isBusinessWP: true,
                  });
                }
              });

              setTimeout(() => {
                this.createThumbnail();
              }, 600);
            }
          };
          getFileInfo();
        } else {
          this.wholeWBData = [];
        }
      })
      .catch((e) => {
        this.createThumbnail();
        console.log(
          'While reading WP Businee getting errors' + JSON.stringify(e)
        );
      });
  }

  createThumbnail() {
    if (this.wholeVideos.length) {
      this.videoEditor
        .createThumbnail({
          fileUri:
            this.wholeVideos[this.indexeeee].path +
            '/' +
            this.wholeVideos[this.indexeeee].name,
          outputFileName: this.wholeVideos[this.indexeeee].name
            .split('.')
            .shift(),
          atTime: 1,
          height: 200,
          quality: 100,
        })
        .then(
          (thumbnail) => {
            //get dataUrl
            let tempPath = thumbnail.split('/');
            let thumbName = tempPath.pop();
            let pathToThumb = 'file://' + tempPath.join('/') + '/';

            this.file
              .readAsDataURL(pathToThumb, thumbName)
              .then((vidThumb) => {
                var data = {
                  video: this.getTrustImg(
                    this.wholeVideos[this.indexeeee].path +
                      '/' +
                      this.wholeVideos[this.indexeeee].name
                  ),
                  thumb: vidThumb,
                  isBusinessWP: this.wholeVideos[this.indexeeee].isBusinessWP,
                  name: this.wholeVideos[this.indexeeee].name,
                };
                this.videos.push(data);

                if (this.wholeVideos.length > this.indexeeee + 1) {
                  this.indexeeee++;
                  this.createThumbnail();
                }
              })
              .catch((err) => {
                console.log(err);
              });
          },
          (error) => {
            console.log('error>>>>>>>>>>>>>>>>>>>>>>>>>>>' + error);
            // this.showAlert('error' + error);
          }
        );
    }
  }

  getTrustImg(imageSrc) {
    const path = (<any>window).Ionic.WebView.convertFileSrc(imageSrc);
    return path;
  }

